#!/bin/bash

# Script to view Railway logs and filter for errors
# Usage: ./view-railway-logs.sh [service-name]

echo "🔍 Fetching Railway logs..."
echo ""

# Check if Railway CLI is installed
if ! command -v railway &> /dev/null; then
    echo "❌ Railway CLI is not installed."
    echo "📦 Install it with: npm install -g @railway/cli"
    echo "🔐 Then login with: railway login"
    exit 1
fi

# Check if logged in
if ! railway whoami &> /dev/null; then
    echo "❌ Not logged in to Railway."
    echo "🔐 Login with: railway login"
    exit 1
fi

SERVICE_NAME=${1:-"backend-api"}

echo "📋 Viewing logs for service: $SERVICE_NAME"
echo "🔍 Filtering for errors, migrations, and database issues..."
